import React from 'react'
import './style.css'
import { store } from '../../store/store.js'
import { ADD_ACTION } from '../../store/action-creators'

export default class Login extends React.Component {
  constructor(props) {
    super(props)
    console.log(store.getState())
    this.state = {
      userID: '1',
      username: '',
      password: ''
    }
  }

  componentDidMount() {

  }
  userChange = (e) => {
    this.setState({
      username: e.target.value && e.target.value.trim()
    })
  }
  passwordChange = (e) => {
    this.setState({
      password: e.target.value && e.target.value.trim()
    })
  }
  //空
  confirmData = (data) => {
    let res = true
    for (let item in data) {
      console.log(data[item].length)
      if (data[item].length === 0) {
        res = false
      }
    }
    console.log('res:' + res)
    return res
  }
  listerner = () => {
    let newState = store.getState();
    this.setState(newState);
  }
  loginIn = (e) => {

    const { userID, username, password } = this.state

    let data = {
      userID: userID,
      username: username,
      password: password
    }
    console.log(data)
    if (!this.confirmData(data)) {
      alert('null')
      return
    }

    const action = ADD_ACTION(data)
    store.dispatch(action)

    store.subscribe(this.listerner)

  }
  result = (e) => {
    window.alert(this.state.password, this.state.password, this.state.userID)
  }
  userIDChange = () => {

  }
  render() {
    const { userID, username, password } = this.state
    return (
      <div className="login">
        <div className="title">注册</div>
        <div className="input-wrap">
          <label htmlFor="userID" className="input-title">用户名ID:</label>
          <input id="userID"
            className="input"
            value={userID}
            onChange={this.userIDChange}
          />
        </div>
        <div className="input-wrap">
          <label htmlFor="user" className="input-title">用户名:</label>
          <input id="user"
            className="input"
            value={username}
            onChange={this.userChange}
          />
        </div>
        <div className="input-wrap">
          <label htmlFor="password" className="input-title">密 码:</label>
          <input id="password"
            className="input"
            onChange={this.passwordChange}
            value={password}
          />
        </div>

        <div className="btn" onClick={this.loginIn}>注册</div>
        <div className="btn result" onClick={this.result}>结果</div>
      </div>
    )
  }
}
