const defaultState = {
  username: "null",
  password: "null"
}
export const reducer = (state = defaultState, action) => {
  console.log(action, state)
  if (action.type === 'init') {
    let newstate = JSON.parse(JSON.stringify(state))
    newstate.username = action.value
    return newstate
  }
  return state
}
