import React from 'react'
import './style.css'
import Login from '../login'
import { createStore } from 'redux'

const reducer = (state, action) => {
  console.log(state, action)
  switch (action.type) {
    case 'INCREMENT': return state.userID + 1;
    case 'DECREMENT': return state.userID - 1;
    default: return state.userID;
  }
}
const store = createStore(reducer)

export default class Home extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      userID: 1
    }
  }
  render() {
    const { userID } = this.state
    return (
      <div className="home">
        <Login
          value={store.getState()}
          onIncrement={() => store.dispatch({ userID, type: 'INCREMENT' })}
          onDecrement={() => store.dispatch({ userID, type: 'DECREMENT' })}
        />
      </div>
    )
  }
}
