import React from 'react'
import './style.css'


export default class Login extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      userID: '',
      username: '',
      password: ''
    }
  }

  componentDidMount() {
    const { value } = this.props
    this.setState({
      userID: value
    })
  }
  userChange = (e) => {
    this.setState({
      username: e.target.value && e.target.value.trim()
    })
  }
  passwordChange = (e) => {
    this.setState({
      password: e.target.value && e.target.value.trim()
    })
  }

  confirmData = (data) => {
    let res = false
    for (let item in data) {
      if (data[item].length === 0) {
        res = true
      }
    }
    return res
  }

  loginIn = (e) => {
    let flag = true

    const { userID, username, password } = this.state
    // if (!userID) {
    //   console.log(userID)
    // }
    let data = {
      userID: userID,
      username: username,
      password: password
    }
    if (this.confirmData(data)) {
      console.log('失败')
      return
    }

    console.log(data)
  }
  render() {
    const { userID } = this.state
    return (
      <div className="login">
        <div className="title">注册</div>
        <div className="input-wrap">
          <label htmlFor="user" className="input-title">用户名:</label>
          <input id="user"
            className="input"
            onChange={this.userChange}
          />
        </div>
        <div className="input-wrap">
          <label htmlFor="password" className="input-title">密 码:</label>
          <input id="password"
            className="input"
            onChange={this.passwordChange}
          />
        </div>
        <div>{userID}</div>
        <div className="btn" onClick={this.loginIn}>注册</div>
      </div>
    )
  }
}
